def getLvl(n=1):
    return course1[n - 1][:-1]


def getPar(course=1):
    if course == 1:
        count = []
        for x in range(9):
            if x < len(course1):
                l = course1[x][-1]
                par = l[0]
                count.append(par)
            else:
                count.append(0)
    return count


def getStart(lvl, course=1):
    if course == 1:
        pos = course1[lvl - 1][-1]
        nPos = pos[-1]
    return nPos


def coinHit(lvl):
    for x in course1[lvl]:
        if len(x) > 4:
            if x[4] == 'coin':
                x[5] = False

